mapper.update:
update stock_my_selected
set selected_type = #{selectedType,jdbcType=BIGINT}
where stock_id = #{stockId,jdbcType=BIGINT} and selected_type = #{selectedType,jdbcType=BIGINT}

mapper.select:
select 
from stock_my_selected
where stock_id = #{stockId,jdbcType=BIGINT} and selected_type = #{selectedType,jdbcType=BIGINT}
