-- ----------------------------
DROP TABLE IF EXISTS `stock_daily_transactions`;
CREATE TABLE `stock_daily_transactions` (
  `id` bigint(20) NOT NULL COMMENT '主鍵',
  `stock_id` bigint(20) NOT NULL COMMENT '股票id',
  `no` int(11) NOT NULL COMMENT '代號',
  `name` varchar(100) NOT NULL COMMENT '商品名稱',
  `account_no` varchar(100) NOT NULL,
  `currency` varchar(20) NOT NULL DEFAULT '' COMMENT '幣別',
  `tx_date` date NOT NULL COMMENT '成交日期',
  `tx_kind` varchar(50) NOT NULL COMMENT '交易種類',
  `quantity` int(11) DEFAULT NULL COMMENT '數量',
  `tx_price` decimal(16,2) DEFAULT NULL COMMENT '成交價格',
  `tx_amount` decimal(16,2) DEFAULT NULL COMMENT '成交金額',
  `fee` decimal(16,2) DEFAULT NULL COMMENT '手續費',
  `tx_tallage` decimal(16,2) DEFAULT NULL COMMENT '交易稅',
  `zs_tallage` decimal(16,2) DEFAULT NULL COMMENT '證所稅',
  `financing_amount` decimal(16,2) DEFAULT NULL COMMENT '融資金額/券擔保品',
  `cash deposit` decimal(16,2) DEFAULT NULL COMMENT '資自備款/券保證金',
  `interest` decimal(16,2) DEFAULT NULL COMMENT '利息',
  `deb_fee` decimal(16,2) DEFAULT NULL COMMENT '借券費',
  `customer_charge` decimal(16,2) DEFAULT NULL COMMENT '客戶淨收付',
  `profit` decimal(16,2) DEFAULT NULL COMMENT '損益',
  `pay_rate` decimal(16,2) DEFAULT NULL COMMENT '報酬率(%)',
  `holding_cost` decimal(16,2) DEFAULT NULL COMMENT '持有成本',
  `create_date` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_date` datetime DEFAULT NULL COMMENT '最后更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `stock_daily_transactions_uq` (`no`,`tx_date`,`tx_kind`,`quantity`,`tx_price`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='每天買買股';

